    private void jPanelEntrarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jPanelEntrarMouseClicked
        char[] senha;
        Funcionario f = new Funcionario();
        f.setUsuario(tUsuario.getText());
        senha = pSenha.getPassword();
        if(!f.getUsuario().isEmpty() && senha.length>0) f.getFromDB();
        else {
            JOptionPane.showMessageDialog(rootPane, "Senha ou nome incorretos!", "Erro!", ERROR_MESSAGE);
            return;
        }
        if(!Arrays.equals(senha, f.getSenha().toCharArray())) JOptionPane.showMessageDialog(rootPane, "Senha ou nome incorretos!", "Erro!", ERROR_MESSAGE);
        else {
            Menu tela = new Menu();
            if(f.getPermissao() != 1) {
                tela.getEmitirPagamentos().setVisible(false);
                tela.getCortarAcesso().setVisible(false);
                tela.getConsultarFuncionarios().setVisible(false);
            }
            tela.setVisible(true);
            this.dispose();
        }
    }//GEN-LAST:event_jPanelEntrarMouseClicked

